import{_ as a,c as t,o as r,ag as i}from"./chunks/framework.B-XtCDNB.js";const s="/assets/fsq-spatial-desktop-earthquakes.Vtdm_i34.webp",o="/assets/flowmap-city.CA35nMv7.webp",n="/assets/cosmograph.COgkNNPk.webp",f=JSON.parse('{"title":"Case Studies","description":"","frontmatter":{"outline":"deep","head":[["link",{"rel":"canonical","href":"https://sqlrooms.org/case-studies.html"}]]},"headers":[],"relativePath":"case-studies.md","filePath":"case-studies.md"}'),l={name:"case-studies.md"};function p(d,e,u,c,m,g){return r(),t("div",null,[...e[0]||(e[0]=[i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /case-studies.md for this page in Markdown format</div><h1 id="case-studies" tabindex="-1">Case Studies <a class="header-anchor" href="#case-studies" aria-label="Permalink to &quot;Case Studies&quot;">​</a></h1><h2 id="foursquare-spatial-desktop" tabindex="-1">Foursquare Spatial Desktop <a class="header-anchor" href="#foursquare-spatial-desktop" aria-label="Permalink to &quot;Foursquare Spatial Desktop&quot;">​</a></h2><p><a href="https://foursquare.com/products/spatial-desktop" target="_blank" rel="noreferrer">Foursquare Spatial Desktop</a> is a powerful geospatial computing tool that transforms your desktop into a comprehensive spatial analysis environment. Built on SQLRooms, it delivers native DuckDB query performance and real-time visualization rendering—all powered natively on your machine without requiring server infrastructure.</p><p><a href="https://foursquare.com/products/spatial-desktop/" target="_blank" rel="noreferrer"><img src="'+s+'" width="450"></a></p><p>Key features include:</p><ul><li><strong>Native DuckDB Performance</strong>: Run complex spatial queries on multi-GB datasets with native DuckDB integration without cloud compute dependence</li><li><strong>Real-time Rendering</strong>: Harness Kepler.gl&#39;s visualization excellence to render millions of points with interactive filtering and smooth animations</li><li><strong>Modern Spatial Formats</strong>: Native support for GeoParquet, PMTiles, and other formats GIS professionals need</li><li><strong>Flexible Data Management</strong>: Save projects locally or to personal cloud storage without internet connectivity requirements</li><li><strong>Offline Capability</strong>: Full analytical power available without cloud dependencies</li></ul><h2 id="flowmap-city" tabindex="-1">Flowmap City <a class="header-anchor" href="#flowmap-city" aria-label="Permalink to &quot;Flowmap City&quot;">​</a></h2><p><a href="https://www.flowmap.city/" target="_blank" rel="noreferrer">Flowmap City</a> is a powerful web-based platform for visualizing and analyzing mobility data and origin-destination flows. The application helps urban planners, transportation analysts, and researchers understand movement patterns in cities and regions.</p><p><a href="https://www.flowmap.city/" target="_blank" rel="noreferrer"><img src="'+o+'" width="400"></a></p><p>The platform enables users to upload their own mobility datasets and create interactive visualizations that can be shared with stakeholders or embedded in other applications.</p><p>Key features include:</p><ul><li><strong>Flow Visualization</strong>: Analyze origin-destination patterns with interactive flow maps</li><li><strong>Mobility Analysis</strong>: Study commuting patterns, transportation demand, and traffic flows</li><li><strong>Temporal Patterns</strong>: Explore how movement patterns change over time and seasons</li><li><strong>Multi-modal Analysis</strong>: Compare different transportation modes and their usage</li><li><strong>Infrastructure Planning</strong>: Make data-driven decisions for transportation infrastructure</li><li><strong>Interactive Filtering</strong>: Filter and analyze specific routes, regions, or time periods</li></ul><h2 id="cosmograph" tabindex="-1">Cosmograph <a class="header-anchor" href="#cosmograph" aria-label="Permalink to &quot;Cosmograph&quot;">​</a></h2><p><a href="https://cosmograph.app/" target="_blank" rel="noreferrer">Cosmograph</a> is a powerful web-based application for visualizing and analyzing large graph datasets and machine learning embeddings. The application runs entirely in the browser, leveraging your GPU for all computations while keeping your data private and secure. The upcoming version of Cosmograph is being built using SQLRooms to enhance its data processing capabilities and analytical features.</p><p><a href="https://run.cosmograph.app/" target="_blank" rel="noreferrer"><img src="'+n+'" width="400"></a></p><p>Key features include:</p><ul><li><strong>Network Graph Visualization</strong>: Analyze complex relationships and patterns in graph data</li><li><strong>ML Embeddings Analysis</strong>: Visualize and explore machine learning embeddings in 2D space</li><li><strong>Temporal Analysis</strong>: Study how relationships and patterns evolve over time</li><li><strong>Community Detection</strong>: Identify clusters and anomalies within your data</li><li><strong>Interactive Analysis</strong>: Use filters and histograms to explore data distributions</li><li><strong>GPU-Accelerated</strong>: Performs all calculations locally using your GPU for optimal performance</li></ul>',18)])])}const y=a(l,[["render",p]]);export{f as __pageData,y as default};
